import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import Loading from "../../components/loader";
import {
  Card,
  CardBody,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Col,
  Container,
  Row,
} from "reactstrap";

export default function Product(props) {
  const [product, setProduct] = useState([]);
  // const [loader, setLoader] = useState(true);
  const history = useHistory();
  const clickHandler = (id) => {
    history.push(`/productDeteil/${id}`);
  };

  useEffect(() => {
    axios
      .get("https://fakestoreapi.herokuapp.com/products")
      .then(function (response) {
        setProduct(response.data);
        // setLoader(false);
        console.log(response.data, "==success");
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);



  return (
    <Container>
      <h1>Llatest product</h1>
      <Row>
        {product.map((product, id) => (
          <Col key={id} sm={12} md={6} lg={4} xl={3}>
            <Card className="my-3 p-3 rounded">
              <CardImg style={{width:"200px",height:"200px"}} src={product.image} alt="Card image cap" />
              <CardBody>
                <CardTitle onClick={() => clickHandler(product.id)} tag="h5">{product.title}</CardTitle>
                <CardSubtitle tag="h6" className="mb-2 text-muted">
                  Card subtitle
                </CardSubtitle>
                <CardText>
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </CardText>
              </CardBody>

            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}
